INSERT INTO `blog` (`id`, `user_id`, `type`, `title`, `image`, `description`, `content`, `created_at`) VALUES
(2, 1, 1, 'Cách nhận biết trung tâm gia sư uy tín', 'Phạm Tiến Thành công1653379984.jpg', 'Thuê gia sư qua các trung tâm đang là hình thức phổ biến được cha mẹ lựa chọn, để giúp con học tập tốt hơn. Bạn có thể tham khảo 7 cách sau để có thể tìm được trung tâm gia sư uy tín chất lượng.', '<div class=\"blog-content\" style=\"box-sizing: border-box; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<h2 id=\"ten_trung_tam_cu_the\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: inherit; line-height: 1.5; color: inherit; font-size: 1.5rem; padding-top: 0.5rem;\">1. T&ecirc;n trung t&acirc;m cụ thể</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem;\">Những trung t&acirc;m gia sư uy t&iacute;n thường sử dụng t&ecirc;n thương hiệu dưới dạng danh từ ri&ecirc;ng, dễ nhớ.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem;\">Bạn n&ecirc;n cẩn thận với những đơn vị sử dụng t&ecirc;n bằng c&aacute;c từ chung chung, kh&oacute; nhớ, dễ g&acirc;y nhầm lẫn. Nếu họ l&agrave;m việc uy t&iacute;n, ắt hẳn họ phải t&igrave;m c&aacute;ch để bạn nhớ tới họ, thay v&igrave; sử dụng một t&ecirc;n gọi sẽ bị l&atilde;ng qu&ecirc;n.</p>\r\n<h2 id=\"co_dia_chi_ro_rang\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: inherit; line-height: 1.5; color: inherit; font-size: 1.5rem; padding-top: 0.5rem;\">2. C&oacute; địa chỉ r&otilde; r&agrave;ng</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem;\">Địa chỉ của những trung t&acirc;m gia sư uy t&iacute;n thường r&otilde; r&agrave;ng, ở những nơi đi lại thuận tiện, dễ t&igrave;m kiếm.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem;\">Bạn n&ecirc;n cảnh gi&aacute;c với những trung t&acirc;m c&oacute; địa chỉ trong ng&otilde; ng&aacute;ch. C&oacute; thể họ chỉ l&agrave; những đơn vị nhỏ lẻ, tạm thời, dẫn đến l&agrave;m việc kh&ocirc;ng chuy&ecirc;n nghiệp.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem;\">Để biết địa chỉ của trung t&acirc;m bạn đang t&igrave;m hiểu, bạn c&oacute; thể t&igrave;m tại phần cuối của website hoặc gọi điện hỏi trực tiếp trung t&acirc;m đ&oacute;.</p>\r\n</div>', '2022-05-24 08:13:04'),
(3, 1, 1, 'Những lưu ý khi thuê gia sư tại Hà Nội', 'Phạm Tiến Thành công1653380840.jpg', 'Tại Hà Nội hình thức học gia sư rất phổ biến được nhiều cha mẹ lựa chọn để đảm bảo và nâng cao kiến thức cho con. Tuy nhiên, việc thuê gia sư sẽ khá khó khăn, nhất là khi bạn chưa có nhiều kinh nghiệm. Dưới đây là những lưu ý giúp bạn có thể thuê được gia sư tại Hà Nội.', '<h3 id=\"quan_cau_giay_dong_da\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Quận Cầu Giấy, Đống Đa</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Tại khu vực n&agrave;y kh&aacute; nhiều gia sư l&agrave; sinh vi&ecirc;n ở c&aacute;c trường như: Đại học Sư phạm H&agrave; Nội, Đại học Quốc Gia H&agrave; Nội, Đại học Thủ đ&ocirc; H&agrave; Nội, Đại học Ngoại Thương, Học viện Ngoại giao,&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-cau-giay\">Gia sư quận Cầu Giấy</a></p>\r\n<h3 id=\"quan_thanh_xuan\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Quận Thanh Xu&acirc;n</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Thu&ecirc; gia sư quận Thanh Xu&acirc;n c&oacute; nhiều lợi thế bởi khu vực n&agrave;y v&agrave; khu vực c&aacute;c quận l&acirc;n cận c&oacute; rất nhiều trường Đại học: Đại học Kinh tế Quốc d&acirc;n, Đại học B&aacute;ch Khoa, Đại học Quốc Gia H&agrave; Nội, Học viện Bưu ch&iacute;nh Viễn th&ocirc;ng, Đại học H&agrave; Nội,&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-thanh-xuan\">Gia sư quận Thanh Xu&acirc;n</a></p>\r\n<h3 id=\"quan_hai_ba_trung_hoang_mai\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Quận Hai B&agrave; Trưng, Ho&agrave;ng Mai</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Tại hai khu vực n&agrave;y bạn c&oacute; thể lựa chọn gia sư l&agrave; sinh vi&ecirc;n c&aacute;c trường nổi tiếng như: Đại học B&aacute;ch Khoa, Đại học Y, Đại học Kinh tế quốc d&acirc;n, Học viện ng&acirc;n h&agrave;ng,&hellip;</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-hai-ba-trung\">Gia sư quận Hai B&agrave; Trưng</a></li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-hoang-mai\">Gia sư quận Ho&agrave;ng Mai</a></li>\r\n</ul>\r\n<h3 id=\"quan_ha_dong\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Quận H&agrave; Đ&ocirc;ng</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Khu vực quận H&agrave; Đ&ocirc;ng cũng c&oacute; kh&aacute; nhiều c&aacute;c trường Đại học như: Đại học H&agrave; Nội, Học viện Bưu ch&iacute;nh viễn th&ocirc;ng, Học viện Y dược cổ truyền,&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-ha-dong\">Gia sư quận H&agrave; Đ&ocirc;ng</a></p>\r\n<h3 id=\"cac_quan_huyen_khac\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">C&aacute;c Quận, Huyện kh&aacute;c</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đ&acirc;y l&agrave; những khu vực xa hoặc c&oacute; &iacute;t trường Đại học. Tuy nhi&ecirc;n bạn vẫn c&oacute; rất nhiều lựa chọn. Bởi c&oacute; nhiều c&aacute;c bạn gia sư đang ở trọ hoặc sinh sống trực tiếp tại đ&acirc;y.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-ba-dinh\">Gia sư quận Ba Đ&igrave;nh</a></li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-quan-hoan-kiem\">Gia sư quận Ho&agrave;n Kiếm</a></li>\r\n</ul>\r\n<h2 id=\"thue_gia_su_theo_mon_hoc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">2. Thu&ecirc; gia sư theo m&ocirc;n học</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Gia sư giỏi kh&ocirc;ng đồng nghĩa l&agrave; gia sư đ&oacute; c&oacute; thể dạy được tất cả c&aacute;c m&ocirc;n. Th&ocirc;ng thường, một gia sư chỉ dạy tốt từ một đến hai m&ocirc;n bởi kiến thức c&aacute;c m&ocirc;n kh&ocirc;ng c&oacute; nhiều sự li&ecirc;n kết.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Việc dạy c&ugrave;ng l&uacute;c nhiều m&ocirc;n sẽ tạo ra một số bất lợi như sau:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Kh&ocirc;ng đảm bảo được kiến thức chuy&ecirc;n s&acirc;u</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">&Iacute;t lựa chọn, v&igrave; kh&ocirc;ng c&oacute; nhiều gia sư dạy được nhiều m&ocirc;n học</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Thời gian thu&ecirc; được gia sư H&agrave; Nội ph&ugrave; hợp sẽ rất l&acirc;u</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">V&igrave; vậy, để việc học gia sư hiệu quả hơn bạn n&ecirc;n t&igrave;m gia sư H&agrave; Nội dựa theo m&ocirc;n học m&agrave; con đang cần sắp xếp.</p>\r\n<h3 id=\"gia_su_tieu_hoc\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Gia sư Tiểu học</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đối với cấp học n&agrave;y, kiến thức kh&aacute; cơ bản v&agrave; c&oacute; sự li&ecirc;n quan. Do vậy,&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-tieu-hoc\">gia sư Tiểu học</a>&nbsp;th&ocirc;ng thường sẽ dạy cả To&aacute;n v&agrave; Tiếng Việt, thậm ch&iacute; cả Tiếng Anh.</p>\r\n<h3 id=\"gia_su_mon_toan\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Gia sư m&ocirc;n To&aacute;n</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đối với m&ocirc;n To&aacute;n việc sắp xếp một gia sư dạy l&agrave; tốt nhất, nhưng nếu con muốn hỗ trợ th&ecirc;m b&agrave;i tập bạn cũng c&oacute; thể t&igrave;m gia sư k&egrave;m th&ecirc;m một trong c&aacute;c m&ocirc;n&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-vat-ly\">Vật L&yacute;</a>&nbsp;hoặc&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-hoa\">H&oacute;a học</a></p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Tuy nhi&ecirc;n, nếu con cần học kiến thức n&acirc;ng cao, c&oacute; mục ti&ecirc;u thi c&aacute;c trường Chuy&ecirc;n tại H&agrave; Nội hoặc thi Đại học, bạn n&ecirc;n t&aacute;ch ri&ecirc;ng c&aacute;c m&ocirc;n học v&agrave; sắp xếp một gia sư tương ứng để đảm bảo hiệu quả cao nhất.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-toan\">C&aacute;ch thu&ecirc; gia sư To&aacute;n đạt hiệu quả cao</a></p>\r\n<h3 id=\"gia_su_mon_tieng_anh\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Gia sư m&ocirc;n Tiếng Anh</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đối với m&ocirc;n Tiếng Anh, bạn n&ecirc;n x&aacute;c định r&otilde; mục ti&ecirc;u của con l&agrave; học theo kiến thức tr&ecirc;n trường hay học để giao tiếp, thi chứng chỉ tiếng Anh. Việc x&aacute;c định r&otilde; mục ti&ecirc;u khi học Tiếng Anh mới gi&uacute;p bạn t&igrave;m được gia sư tại H&agrave; Nội ph&ugrave; hợp cho con.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-tieng-anh\">6 lưu &yacute; gi&uacute;p thu&ecirc; gia sư Tiếng Anh hiệu quả</a></p>\r\n<h3 id=\"gia_su_mon_van\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Gia sư m&ocirc;n Văn</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đ&acirc;y l&agrave; một m&ocirc;n học kh&aacute; đặc biệt, đ&ograve;i hỏi nhiều kĩ năng v&agrave; sự đam m&ecirc;. Do vậy,&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-van\">gia sư m&ocirc;n Văn</a>&nbsp;thường kh&ocirc;ng dạy th&ecirc;m c&aacute;c m&ocirc;n như To&aacute;n, Vật L&yacute;, Tiếng Anh,&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">D&ugrave; vậy, tại H&agrave; Nội, bạn vẫn c&oacute; thể t&igrave;m một gia sư Văn k&egrave;m th&ecirc;m một trong c&aacute;c m&ocirc;n khoa học x&atilde; hội như:&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-lich-su\">Lịch Sử</a>,&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/gia-su-dia-ly\">Địa L&yacute;</a>, Gi&aacute;o dục c&ocirc;ng d&acirc;n.</p>\r\n<h2 id=\"can_nhac_gia_su_la_giao_vien_hay_sinh_vien_moi_phu_hop\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">3. C&acirc;n nhắc gia sư l&agrave; gi&aacute;o vi&ecirc;n hay sinh vi&ecirc;n mới ph&ugrave; hợp</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Gia sư H&agrave; Nội bao gồm c&aacute;c bạn sinh vi&ecirc;n, gi&aacute;o vi&ecirc;n tự do hoặc gi&aacute;o vi&ecirc;n đang dạy tại trường. Việc học với gia sư l&agrave; gi&aacute;o vi&ecirc;n hay sinh vi&ecirc;n sẽ c&oacute; ưu v&agrave; nhược điểm nhất định.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Gi&aacute;o vi&ecirc;n:</span>&nbsp;C&oacute; nhiều kinh nghiệm giảng dạy, phong th&aacute;i nghi&ecirc;m t&uacute;c, khiến con tự gi&aacute;c nghe lời nhưng điều đ&oacute; cũng dễ tạo n&ecirc;n &aacute;p lực cho con.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Sinh vi&ecirc;n:</span>&nbsp;C&oacute; sự th&ocirc;ng minh s&aacute;ng tạo, c&aacute;ch tiếp cận kiến thức mới mẻ v&agrave; dễ tạo sự gần gũi với con. Nhưng lại kh&aacute; kh&oacute; khăn khi dạy học sinh c&aacute; biệt, kh&ocirc;ng nghe lời.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Bạn n&ecirc;n c&acirc;n nhắc kĩ theo c&aacute;c yếu tố về t&iacute;nh c&aacute;ch của con, thời gian học, điều kiện kinh tế,&hellip; để đảm bảo việc học gia sư H&agrave; Nội hiệu quả.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Xem&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/blog/chon-gia-su-giao-vien-hay-sinh-vien\">N&ecirc;n chọn gia sư l&agrave; gi&aacute;o vi&ecirc;n hay sinh vi&ecirc;n?</a></p>\r\n<h2 id=\"kiem_tra_kien_thuc_cua_gia_su\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">4. Kiểm tra kiến thức của gia sư</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Việc kiểm tra kiến thức của gia sư trước khi cho con học rất quan trọng. H&atilde;y đảm bảo gia sư đ&oacute; c&oacute; đủ kiến thức để dạy con.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Nếu bạn tự t&igrave;m gia sư, h&atilde;y chuẩn bị trước 1 b&agrave;i kiểm tra ngắn.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Nếu bạn thu&ecirc; gia sư H&agrave; Nội tại c&aacute;c trung t&acirc;m uy t&iacute;n, những đơn vị n&agrave;y sẽ kiểm tra, đ&aacute;nh gi&aacute; kiến thức của gia sư trước khi giới thiệu đến bạn.</li>\r\n</ul>\r\n<h2 id=\"ban_nen_di_tiep_nhu_the_nao\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Bạn n&ecirc;n đi tiếp như thế n&agrave;o</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nếu bạn tự t&igrave;m thu&ecirc; gia sư tại H&agrave; Nội, bạn sẽ phải bỏ ra rất nhiều c&ocirc;ng sức v&agrave; thời gian. H&atilde;y li&ecirc;n hệ đến c&aacute;c&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/\">trung t&acirc;m gia sư uy t&iacute;n</a>&nbsp;để được hỗ trợ trong việc t&igrave;m gia sư ph&ugrave; hợp cho con.</p>', '2022-05-24 08:27:20'),
(4, 1, 1, 'Gia sư dạy đàn tại nhà', 'Phạm Tiến Thành công1653380968.jpg', 'Học đàn là một trong những môn năng khiếu phổ biến được nhiều phụ huynh lựa chọn cho con. Có nhiều hình thức để con có thể học môn năng khiếu này trong đó có gia sư dạy tại nhà.', '<h2 id=\"gia_su_day_la_ai\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Gia sư dạy l&agrave; ai?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Chủ yếu l&agrave; c&aacute;c bạn sinh vi&ecirc;n đang theo học hoặc vừa tốt nghiệp tại c&aacute;c Nhạc viện, c&oacute; chuy&ecirc;n m&ocirc;n v&agrave; kinh nghiệm dạy k&egrave;m tại nh&agrave;.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ngo&agrave;i ra, cũng c&oacute; gia sư l&agrave; c&aacute;c bạn sinh vi&ecirc;n đ&atilde; chơi đ&agrave;n nhiều năm, c&oacute; kĩ năng, tham gia v&agrave; đạt c&aacute;c giải thưởng tại c&aacute;c cuộc thi &acirc;m nhạc.</p>\r\n<h2 id=\"noi_dung_hoc_nhung_gi\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Nội dung học những g&igrave;?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Gia sư sẽ đ&aacute;nh gi&aacute; năng lực của con để c&oacute; gi&aacute;o tr&igrave;nh ph&ugrave; hợp v&agrave; hiệu quả nhất. Nếu con chưa biết g&igrave; hoặc mới bắt đầu học, gia sư sẽ dạy từ sơ cấp, c&aacute;c kiến thức cơ bản nhất.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ngo&agrave;i c&aacute;c b&agrave;i giảng l&iacute; thuyết, gia sư sẽ hướng dẫn con thực h&agrave;nh trực tiếp với đ&agrave;n, c&oacute; c&aacute;c b&agrave;i tập sau buổi học để con luyện tập thường xuy&ecirc;n.</p>\r\n<h2 id=\"thoi_gian_buoi_hoc_bao_lau\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Thời gian buổi học bao l&acirc;u?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Một buổi học diễn ra trong khoảng một giờ. Kh&ocirc;ng giống như học c&aacute;c m&ocirc;n văn h&oacute;a, con phải li&ecirc;n tục d&ugrave;ng tay để luyện tập. Do vậy, nếu con học l&acirc;u hơn, con sẽ dễ bị mỏi tay v&agrave; ch&aacute;n nản.</p>\r\n<h2 id=\"hoc_phi_nhu_the_nao\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Học ph&iacute; như thế n&agrave;o?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học ph&iacute; gia sư dạy đ&agrave;n 1 k&egrave;m 1 tại nh&agrave; sẽ được t&iacute;nh theo buổi học. Cụ thể:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Đ&agrave;n Piano, Organ th&ocirc;ng thường từ 250.000 đồng/buổi</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Đ&agrave;n Guitar,Violin,... th&ocirc;ng thường từ 300.000 đồng/buổi</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học ph&iacute; phụ thuộc v&agrave;o mức học hiện tại v&agrave; mục ti&ecirc;u của con. Nếu con đ&atilde; chơi đ&agrave;n từ l&acirc;u, c&oacute; mong muốn đạt được tr&igrave;nh độ cao để tham gia c&aacute;c k&igrave; thi, bạn n&ecirc;n c&acirc;n nhắc học ph&iacute; cao hơn để t&igrave;m được gia sư giỏi, ph&ugrave; hợp với năng lực của con.</p>\r\n<h2 id=\"can_luu_y_nhung_gi_de_dat_hieu_qua_khi_hoc_gia_su_tai_nha\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Cần lưu &yacute; những g&igrave; để đạt hiệu quả khi học gia sư tại nh&agrave;?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Để c&oacute; được hiệu quả tốt nhất cho con, bạn cần phải đảm bảo những điều sau:</p>\r\n<h3 id=\"dam_bao_dan_cua_con_hoat_dong_tot\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">1. Đảm bảo đ&agrave;n của con hoạt động tốt</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Buổi học sẽ kh&ocirc;ng thể diễn ra nếu đ&agrave;n của con kh&ocirc;ng hoạt động tốt. Thường xuy&ecirc;n kiểm tra c&aacute;c bộ phận của đ&agrave;n, đặc biệt l&agrave; khi bạn đ&atilde; mua v&agrave; sử dụng từ l&acirc;u.</p>\r\n<h3 id=\"cho_con_thuc_hanh_thuong_xuyen\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">2. Cho con thực h&agrave;nh thường xuy&ecirc;n</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Con sẽ kh&ocirc;ng tiến bộ nếu chỉ học l&iacute; thuyết, bạn n&ecirc;n thường xuy&ecirc;n trao đổi với gia sư để nắm được c&aacute;c b&agrave;i tập, sắp xếp thời gian hợp l&iacute; để con tự luyện tập với đ&agrave;n.</p>\r\n<h3 id=\"tao_su_thich_thu_va_dong_luc_cho_con\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">3. Tạo sự th&iacute;ch th&uacute; v&agrave; động lực cho con</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Để học một m&ocirc;n năng khiếu, con cần nhiều đam m&ecirc; v&agrave; sự th&iacute;ch th&uacute;. Ngo&agrave;i việc học với gia sư, bạn n&ecirc;n chơi c&ugrave;ng con, thường xuy&ecirc;n khen ngợi, cổ vũ con. Như vậy, con sẽ th&iacute;ch th&uacute; v&agrave; c&oacute; động lực để theo đuổi m&ocirc;n năng khiếu n&agrave;y.</p>\r\n<h2 id=\"ban_co_the_di_tiep_nhu_the_nao\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Bạn c&oacute; thể đi tiếp như thế n&agrave;o?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">H&atilde;y tham khảo c&aacute;c&nbsp;<a style=\"box-sizing: border-box; color: #007bff; text-decoration-line: none; background-color: transparent;\" href=\"https://www.growgreen.edu.vn/\">trung t&acirc;m gia sư uy t&iacute;n</a>&nbsp;để đảm bảo bạn c&oacute; được gia sư tốt nhất cho con.</p>', '2022-05-24 08:29:28'),
(6, 10, 2, 'Luyện giọng nói - việc quan trọng khi đi dạy gia sư', 'Nguyễn Vũ Linh Chi1653387367.jpg', 'Khi đi dạy gia sư, bên cạnh việc chuẩn bị kiến thức sao cho tốt, ứng xử với học sinh, phụ huynh sao cho phù hợp, thì việc điều khiển giọng nói sao cho truyền cảm cũng cực kỳ quan trọng.', '<h2 id=\"tai_sao_giong_noi_lai_quan_trong_khi_di_day_gia_su\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Tại sao giọng n&oacute;i lại quan trọng khi đi dạy gia sư?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Dạy gia sư l&agrave; qu&aacute; tr&igrave;nh bạn sẽ truyền đạt kiến thức cho học sinh. Giữa thầy c&ocirc; v&agrave; học tr&ograve; cần c&oacute; sự giao tiếp, tương t&aacute;c bằng lời n&oacute;i, &aacute;nh mắt. Giọng n&oacute;i sẽ ảnh hưởng đến việc tiếp thu b&agrave;i học cũng như ấn tượng của học sinh với phụ huynh.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Nếu bạn c&oacute; giọng n&oacute;i r&otilde; r&agrave;ng, truyền cảm, học sinh sẽ dễ d&agrave;ng tiếp thu v&agrave; th&iacute;ch th&uacute; hơn với việc học. Ngược lại, nếu n&oacute;i qu&aacute; nhỏ, kh&ocirc;ng c&oacute; điểm nhấn, học sinh thường ch&aacute;n, dễ buồn ngủ.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Giọng n&oacute;i hay, nhẹ nh&agrave;ng khiến học sinh c&oacute; ấn tượng tốt hơn với gia sư, cảm thấy th&acirc;n thuộc, gần gũi v&agrave; thoải m&aacute;i hơn khi tr&ograve; chuyện.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Phụ huynh v&agrave; học sinh thường tối kỵ với những gia sư n&oacute;i ngọng. Việc giao tiếp thường xuy&ecirc;n với những người bị ngọng khiến ng&ocirc;n ngữ của học sinh bị ảnh hưởng, cũng c&oacute; thể c&aacute;c em bị l&acirc;y n&oacute;i ngọng theo, nhất l&agrave; với học sinh tiểu học.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">N&oacute;i giọng địa phương cũng l&agrave; r&agrave;o cản với gia sư, đặc biệt những người c&oacute; giọng địa phương nặng. Điều n&agrave;y khiến học sinh vừa kh&oacute; nghe, lại vừa c&oacute; cảm gi&aacute;c kh&oacute; chịu khi học b&agrave;i. Học sinh ở cấp tiểu học cũng c&oacute; thể bị nhiễm c&aacute;ch ph&aacute;t &acirc;m v&ugrave;ng miền.</p>\r\n<h2 id=\"khi_di_gia_su_can_chu_y_luyen_giong_nhu_the_nao\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Khi đi gia sư, cần ch&uacute; &yacute; luyện giọng như thế n&agrave;o?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Giọng n&oacute;i qu&aacute; nhỏ, qu&aacute; kh&ocirc; cứng, n&oacute;i ngọng hay giọng v&ugrave;ng miền đều l&agrave; r&agrave;o cản trong việc dạy gia sư. Tuy nhi&ecirc;n, đ&acirc;y l&agrave; vấn đề bản th&acirc;n c&aacute;c gia sư c&oacute; thể cải thiện được, n&ecirc;n bạn kh&ocirc;ng cần qu&aacute; lo lắng. Khi luyện giọng, bạn cần ch&uacute; &yacute;:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Giọng đủ to, r&otilde; r&agrave;ng:</span>&nbsp;bạn h&atilde;y tự giảng b&agrave;i tại nh&agrave;, giảng trước gương hoặc giảng trước bạn b&egrave;. Ban đầu, bạn c&oacute; thể giảng chậm, vừa giảng vừa nh&igrave;n khẩu h&igrave;nh của m&igrave;nh, lắng nghe giọng n&oacute;i của m&igrave;nh để điều chỉnh.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Giọng truyền cảm:</span>&nbsp;Sau khi luyện được giọng to, r&otilde; r&agrave;ng, bạn c&ograve;n phải luyện c&aacute;ch truyền đạt thật truyền cảm, c&oacute; điểm nhấn. H&atilde;y nhấn nh&aacute; v&agrave;o những c&acirc;u chữ quan trọng để học sinh khắc s&acirc;u trong t&acirc;m tr&iacute;.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Luyện n&oacute;i ngọng:</span>&nbsp;L/n l&agrave; hai &acirc;m nhiều bạn hay ngọng nhất. C&aacute;c bạn n&ecirc;n luyện bằng c&aacute;ch đọc to c&aacute;c văn bản. Đến mỗi chữ c&oacute; chứa &acirc;m đầu l/n bạn cần đọc chậm lại để suy nghĩ xem đọc thế n&agrave;o cho đ&uacute;ng. C&oacute; thể đọc đi đọc lại &acirc;m đ&oacute; cho đến khi thuần thục. Khi đ&atilde; tốt hơn, bạn n&ecirc;n đọc với tốc độ nhanh hơn.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Sửa giọng địa phương:</span>&nbsp;Giọng địa phương kh&ocirc;ng xấu, nhưng để đi dạy th&igrave; bạn n&ecirc;n sửa sớm. Bạn n&ecirc;n nghe c&aacute;ch ph&aacute;t &acirc;m chuẩn của c&aacute;c bạn kh&aacute;c, từ đ&oacute; học n&oacute;i theo. Việc sửa giọng địa phương cần luyện thường xuy&ecirc;n trong lời n&oacute;i, giao tiếp h&agrave;ng ng&agrave;y.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ngo&agrave;i việc luyện giọng, để qu&aacute; tr&igrave;nh giảng b&agrave;i được lưu lo&aacute;t, bạn cần phải soạn gi&aacute;o &aacute;n kỹ trước khi đi dạy. Như vậy, qu&aacute; tr&igrave;nh giảng b&agrave;i sẽ kh&ocirc;ng bị thụ động. Khi thụ động, bạn sẽ cảm thấy lo lắng, b&iacute; từ n&ecirc;n rất kh&oacute; để c&oacute; thể giảng tr&ocirc;i chảy hay truyền cảm.</p>', '2022-05-24 10:16:07');
INSERT INTO `blog` (`id`, `user_id`, `type`, `title`, `image`, `description`, `content`, `created_at`) VALUES
(7, 10, 2, 'Làm sao để tự tin khi đi dạy gia sư', 'Nguyễn Vũ Linh Chi1653387440.png', 'Để là một gia sư giỏi, ngoài yếu tố kiến thức, bạn phải là một người tự tin. Tự tin sẽ giúp truyền đạt kiến thức một cách rõ ràng, khiến học sinh cảm thấy tin tưởng vào những gì đang được học. Vậy làm thế nào để bạn tự tin hơn khi đi dạy gia sư?', '<h2 id=\"luon_trau_doi_kien_thuc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">1. Lu&ocirc;n trau dồi kiến thức</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nếu bạn nắm chắc kiến thức cần giảng dạy, bạn đ&atilde; c&oacute; thể tự tin đến 50% khi đi dạy gia sư. Chỉ khi bạn c&oacute; đủ kiến thức, bạn mới c&oacute; thể truyền đạt cho học sinh, gi&uacute;p học sinh tiến bộ hơn.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ngo&agrave;i kiến thức về m&ocirc;n học, trau dồi kiến thức x&atilde; hội, tự nhi&ecirc;n, khoa học cũng l&agrave; lợi thế, gi&uacute;p bạn kh&ocirc;ng gặp l&uacute;ng t&uacute;ng trước c&acirc;u hỏi của học sinh. Những kiến thức ngo&agrave;i lề n&agrave;y c&ograve;n gi&uacute;p bạn thay đổi kh&ocirc;ng kh&iacute; vốn đang căng thẳng của buổi học, khiến học sinh thoải m&aacute;i hơn.</p>\r\n<h2 id=\"ren_luyen_ky_nang_giang_day\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">2. R&egrave;n luyện kỹ năng giảng dạy</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Bạn c&oacute; kiến thức th&ocirc;i chưa đủ, l&agrave;m sao để truyền đạt hết kiến thức ấy cho học sinh cũng v&ocirc; c&ugrave;ng quan trọng. Bạn sẽ cảm thấy ra sao khi d&ugrave; m&igrave;nh giỏi nhưng lại lu&ocirc;n lắp bắp kh&ocirc;ng thể n&oacute;i để học sinh hiểu.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Để tự tin khi giảng b&agrave;i, bạn phải n&oacute;i thật lưu lo&aacute;t, r&agrave;nh mạch, dứt kho&aacute;t. Với những m&ocirc;n x&atilde; hội, lời giảng giải lại cần truyền cảm v&agrave; c&oacute; chiều s&acirc;u.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nếu bạn kh&ocirc;ng c&oacute; khả năng giao tiếp bẩm sinh hoặc lỡ n&oacute;i ngọng hay giọng v&ugrave;ng miền, bạn c&oacute; thể tự tập luyện bằng c&aacute;ch cầm quyển s&aacute;ch đọc to, r&otilde; r&agrave;ng v&agrave; chậm r&atilde;i.</p>\r\n<h2 id=\"luon_co_su_chuan_bi_truoc_khi_di_day\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">3. Lu&ocirc;n c&oacute; sự chuẩn bị trước khi đi dạy</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Việc bạn đến tay kh&ocirc;ng, giao b&agrave;i tập trong s&aacute;ch gi&aacute;o khoa l&agrave; việc ho&agrave;n to&agrave;n kh&ocirc;ng n&ecirc;n. Đừng nghĩ rằng dạy gia sư th&igrave; kh&ocirc;ng cần soạn b&agrave;i. Trong đầu bạn kh&ocirc;ng phải l&uacute;c n&agrave;o cũng c&oacute; thể lập tr&igrave;nh sẵn kiến thức cho 2 giờ học.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Những việc bạn cần chuẩn bị trước khi dạy:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Đọc trước b&agrave;i cần dạy, soạn đề, soạn nội dung cần dạy</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Soạn sẵn đ&aacute;p &aacute;n c&aacute;c b&agrave;i tập m&agrave; bạn sẽ giao cho học sinh chứ kh&ocirc;ng đến rồi mới ngồi giải c&ugrave;ng học sinh.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Chuẩn bị sẵn tr&igrave;nh tự dạy học, căn thời gian cho ph&ugrave; hợp.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Chuẩn bị cả những c&acirc;u chuyện hoặc vấn đề ngo&agrave;i lề bạn muốn nhắc tới.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Khi mọi thứ đ&atilde; được chuẩn bị kỹ lưỡng, bạn sẽ tự tin đi dạy m&agrave; kh&ocirc;ng lo thừa, thiếu thời gian hay giờ học bị nh&agrave;m ch&aacute;n nữa.</p>\r\n<h2 id=\"chuan_bi_ve_trang_phuc_tac_phong\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">4. Chuẩn bị về trang phục, t&aacute;c phong</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Khi bạn c&oacute; một ngoại h&igrave;nh ổn, bạn sẽ cảm thấy tự tin hơn khi đi dạy. Nhất l&agrave; khi học sinh v&agrave; phụ huynh cũng kh&aacute; để &yacute; vấn đề n&agrave;y.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Mặc trang phục chỉnh tề, lịch sự;</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Đầu t&oacute;c gọn g&agrave;ng;</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Soi gương kiểm tra trước khi đi dạy.</li>\r\n</ul>\r\n<h2 id=\"luon_quyet_doan_truoc_hoc_sinh\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">5. Lu&ocirc;n quyết đo&aacute;n trước học sinh</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nhiều bạn gia sư gặp phải học sinh tai qu&aacute;i thưởng bị \"hẫng\" v&agrave; bỗng trở n&ecirc;n k&eacute;m tự tin. C&aacute;c bạn thường lo sợ rằng học sinh sẽ n&oacute;i xấu m&igrave;nh trước phụ huynh.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Thực tế, bạn c&agrave;ng tỏ ra nh&acirc;n nhượng, học sinh sẽ c&agrave;ng được thể \"lấn lướt\". Việc bạn cần l&agrave;m l&agrave; tỏ th&aacute;i độ quyết đo&aacute;n, nghi&ecirc;m khắc. H&atilde;y cho học sinh của bạn biết rằng: Học sinh l&agrave; người học, c&ograve;n bạn l&agrave; người dạy.</p>\r\n<h2 id=\"loi_ket\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Lời kết:</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Hơn tất cả, kinh nghiệm mới l&agrave; yếu tố quan trọng nhất. C&agrave;ng va vấp nhiều, bản lĩnh của bạn c&agrave;ng được t&ocirc;i luyện. Sau n&agrave;y khi ra trường, đ&agrave;m ph&aacute;n hay thương thuyết hợp đồng mới gọi l&agrave; bản lĩnh thực sự. C&ograve;n gia sư mới chỉ l&agrave; bước khởi đầu để bạn trưởng th&agrave;nh, vậy th&igrave; tại sao bạn lại thiếu tự tin trước c&ocirc;ng việc n&agrave;y được chứ?</p>', '2022-05-24 10:17:20'),
(8, 10, 2, 'Cách giảng dạy cho học sinh lười học', 'Nguyễn Vũ Linh Chi1653387501.jpg', 'Có nhiều lý do phụ huynh mời gia sư đến nhà để giảng dạy. Nhưng lý do chủ yếu là bởi học sinh lười học, dẫn tới học kém. Vậy làm thế nào để bạn có thể giảng dạy tốt cho những học sinh lười học này?', '<h2 id=\"bieu_hien_cua_hoc_sinh_luoi_hoc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Biểu hiện của học sinh lười học:</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đối tượng học sinh n&agrave;y kh&ocirc;ng kh&oacute; để nhận biết bởi c&aacute;c em đều c&oacute; đặc điểm chung:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Kh&ocirc;ng th&iacute;ch học, kh&ocirc;ng tập trung trong giờ học.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Thường t&igrave;m c&aacute;ch tho&aacute;i th&aacute;c việc học để xin nghỉ học: bị ốm, đau bụng, mệt,...</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Trong giờ học, hay lấy l&yacute; do để c&acirc;u giờ: xin đi ra ngo&agrave;i, xin đi uống nước, đi vệ sinh, buồn ngủ,...</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Kh&ocirc;ng bao giờ l&agrave;m b&agrave;i tập về nh&agrave; hoặc l&agrave;m một c&aacute;ch đối ph&oacute;.</li>\r\n</ul>\r\n<h2 id=\"lam_the_nao_de_hoc_sinh_bot_luoi\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">L&agrave;m thế n&agrave;o để học sinh bớt lười?</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nếu ki&ecirc;n tr&igrave; thực hiện những điều sau đ&acirc;y, bạn c&oacute; thể gi&uacute;p học sinh bớt lười v&agrave; th&ecirc;m y&ecirc;u th&iacute;ch việc học h&agrave;nh.</p>\r\n<h3 id=\"ban_phai_kien_nhan\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">1. Bạn phải ki&ecirc;n nhẫn</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Việc khiến học sinh thay đổi từ một học sinh lười sang một học sinh chăm chỉ kh&ocirc;ng thể thực hiện ng&agrave;y một ng&agrave;y hai. Do đ&oacute;, bạn cần ki&ecirc;n nhẫn từng ng&agrave;y, kh&ocirc;ng n&ecirc;n n&oacute;ng vội từ bỏ lớp hoặc c&oacute; th&aacute;i độ n&oacute;ng giận tột độ với học sinh.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Việc bạn n&oacute;ng giận, c&aacute;u gắt sẽ chỉ khiến học sinh sợ h&atilde;i, mệt mỏi v&agrave; ch&aacute;n học th&ecirc;m m&agrave; th&ocirc;i.</p>\r\n<h3 id=\"tao_bau_khong_khi_hoc_thoai_mai\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">2. Tạo bầu kh&ocirc;ng kh&iacute; học thoải m&aacute;i</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Muốn học sinh hứng th&uacute; học, bạn cần khiến c&aacute;c em cảm thấy thoải m&aacute;i, kh&ocirc;ng bị &aacute;p lực. Ban đầu, đừng n&ecirc;n y&ecirc;u cầu khắt khe về th&agrave;nh t&iacute;ch của c&aacute;c em. H&atilde;y tập trung chủ yếu v&agrave;o việc cung cấp kiến thức cơ bản để c&aacute;c em kh&ocirc;ng bị mất gốc.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Giữa giờ học sẽ c&oacute; thời gian giải lao. Trong thời gian đ&oacute; h&atilde;y tr&ograve; chuyện với c&aacute;c em một c&aacute;ch thoải m&aacute;i về cuộc sống. Kh&ocirc;ng n&ecirc;n tiếp tục nhắc đến b&agrave;i vở.</p>\r\n<h3 id=\"tim_dung_phuong_phap_day\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">3. T&igrave;m đ&uacute;ng phương ph&aacute;p dạy</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học sinh lười chưa chắc đ&atilde; phải l&agrave; học sinh k&eacute;m th&ocirc;ng minh. Thực ra, nhiều học sinh tiếp thu b&agrave;i rất nhanh, chỉ l&agrave; ch&uacute;ng lười viết, lười suy nghĩ. Bạn h&atilde;y đ&aacute;nh gi&aacute; xem học sinh lười l&agrave;m g&igrave;, th&iacute;ch th&uacute; với điều g&igrave; để điều chỉnh cho ph&ugrave; hợp.</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Nếu học sinh th&iacute;ch nghe, lười viết:</span>&nbsp;h&atilde;y giảng thật hay v&agrave; dễ hiểu để học sinh c&oacute; thể hiểu b&agrave;i v&agrave; nhớ l&acirc;u.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\"><span style=\"box-sizing: border-box; font-weight: bolder;\">Nếu học sinh lười đọc</span>: h&atilde;y cho học sinh r&egrave;n luyện tư duy nhiều hơn.</li>\r\n</ul>\r\n<h3 id=\"day_kien_thuc_vua_tam\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">4. Dạy kiến thức vừa tầm</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Bạn chớ dạy lượng kiến thức lớn v&agrave; kh&oacute; ngay lập tức. Cần đ&aacute;nh gi&aacute; tr&igrave;nh độ của c&aacute;c em để giao b&agrave;i tập vừa tầm v&agrave; n&acirc;ng cao dần.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học sinh lười gặp b&agrave;i kh&oacute; sẽ c&agrave;ng lười suy nghĩ v&agrave; c&agrave;ng ch&aacute;n học bởi cảm thấy bản th&acirc;n kh&ocirc;ng hiểu g&igrave;.</p>\r\n<h3 id=\"dong_vien_va_khen_ngoi_kip_thoi\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">5. Động vi&ecirc;n v&agrave; khen ngợi kịp thời</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học sinh lười, học k&eacute;m thường đi liền với tự ti. C&aacute;c em c&oacute; th&agrave;nh t&iacute;ch k&eacute;m n&ecirc;n gần như kh&ocirc;ng bao giờ được sự khen ngợi về m&ocirc;n đ&oacute;. Ch&iacute;nh v&igrave; vậy, ngay khi c&aacute;c em c&oacute; những tiến bộ d&ugrave; nhỏ, h&atilde;y d&agrave;nh lời khen ngợi để cổ vũ tinh thần.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nếu c&aacute;c em đ&atilde; cố gắng nhưng điểm chưa thực sự tốt, th&igrave; kh&ocirc;ng n&ecirc;n ch&ecirc; tr&aacute;ch bởi c&aacute;c em ấy đ&atilde; c&oacute; sự cố gắng, chỉ l&agrave; kiến thức chưa thật vững.</p>\r\n<h3 id=\"gan_gui_va_tam_su_voi_hoc_sinh\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">6. Gần gũi v&agrave; t&acirc;m sự với học sinh</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Khi bạn gần gũi v&agrave; được học sinh tin tưởng, bạn sẽ dễ d&agrave;ng t&igrave;m hiểu được l&yacute; do v&igrave; sao em kh&ocirc;ng th&iacute;ch học, l&agrave;m thế n&agrave;o để em th&iacute;ch học hơn. Gia sư h&atilde;y tranh thủ t&igrave;nh cảm n&agrave;y m&agrave; khai th&aacute;c th&ecirc;m những th&ocirc;ng tin. Nhờ đ&oacute; c&oacute; thể thay đổi phương ph&aacute;p ph&ugrave; hợp nhất.</p>\r\n<h3 id=\"han_che_giao_nhieu_bai_tap_ve_nha\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">7. Hạn chế giao nhiều b&agrave;i tập về nh&agrave;</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">C&aacute;c em học sinh lười thường &iacute;t khi l&agrave;m b&agrave;i tập m&agrave; gia sư giao, bởi:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">B&agrave;i tập c&aacute;c c&ocirc; gi&aacute;o ở lớp giao cũng kh&aacute; nhiều, c&aacute;c em kh&ocirc;ng thể ho&agrave;n th&agrave;nh kịp.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">C&aacute;c em học tr&ecirc;n lớp, học gia sư rồi lại phải l&agrave;m b&agrave;i tập cho h&ocirc;m sau n&ecirc;n kh&ocirc;ng kịp thời gian, lu&ocirc;n cảm thấy mệt mỏi.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">C&aacute;c em sẽ d&agrave;nh nhiều thời gian để học c&aacute;c m&ocirc;n m&agrave; c&aacute;c em y&ecirc;u th&iacute;ch.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">B&agrave;i tập tr&ecirc;n lớp l&agrave; bắt buộc c&ograve;n b&agrave;i gia sư giao c&oacute; thể kh&ocirc;ng ho&agrave;n th&agrave;nh cũng kh&ocirc;ng sao.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Vậy n&ecirc;n bạn đừng giao cho học sinh nhiều b&agrave;i tập. Bạn chỉ cần t&igrave;m c&aacute;ch để học sinh hiểu được b&agrave;i ngay trong giờ dạy. Nếu c&oacute; giao, h&atilde;y giao b&agrave;i thật ngắn v&agrave; vừa sức.</p>\r\n<h3 id=\"hay_cho_hoc_sinh_biet_mon_hoc_do_co_tac_dung_gi\" class=\"text-primary\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #005ea2; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">8. H&atilde;y cho học sinh biết, m&ocirc;n học đ&oacute; c&oacute; t&aacute;c dụng g&igrave;?</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Nhiều khi học sinh hỏi với trạng th&aacute;i ch&aacute;n chường: Tại sao em phải học m&ocirc;n n&agrave;y? Nhiệm vụ của bạn ch&iacute;nh l&agrave; giải th&iacute;ch cho c&aacute;c em mục đ&iacute;ch của m&ocirc;n học đ&oacute; l&agrave; g&igrave;. Một số mục đ&iacute;ch của m&ocirc;n học:</p>\r\n<ul style=\"box-sizing: border-box; margin-top: 1rem; margin-bottom: 1.5rem; padding-left: 0px; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Li&ecirc;n quan đến điểm tổng kết, khả năng l&ecirc;n lớp, chọn trường của c&aacute;c em.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">Lợi &iacute;ch của m&ocirc;n học đối với thi đại học, sự nghiệp, trong x&atilde; hội.</li>\r\n<li style=\"box-sizing: border-box; list-style-type: square; list-style-position: outside; margin-left: 2em; margin-bottom: 0.5em;\">N&ecirc;u ra những dẫn chứng về nh&acirc;n vật nổi tiếng đ&atilde; th&agrave;nh c&ocirc;ng nhờ m&ocirc;n học đ&oacute;.</li>\r\n</ul>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">H&atilde;y t&igrave;m ra những lợi &iacute;ch ch&iacute;nh đ&aacute;ng m&agrave; c&aacute;c em kh&ocirc;ng thể chối c&atilde;i. Như vậy, c&aacute;c em sẽ kh&ocirc;ng c&ograve;n ca th&aacute;n về m&ocirc;n học v&ocirc; vị n&agrave;y nữa.</p>\r\n<h2 id=\"loi_ket\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">Lời kết</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Bản th&acirc;n gia sư cũng từng l&agrave; học sinh, bạn chắc chắn cũng đ&atilde; từng kh&ocirc;ng th&iacute;ch một m&ocirc;n học n&agrave;o đ&oacute;. H&atilde;y đặt m&igrave;nh v&agrave;o vị tr&iacute; của học sinh để hiểu c&aacute;c em nghĩ g&igrave; v&agrave; muốn g&igrave;. Từ đ&oacute; c&oacute; những c&aacute;ch thức ph&ugrave; hợp để gi&uacute;p c&aacute;c em học sinh bớt lười hơn.</p>', '2022-05-24 10:18:21'),
(9, 10, 2, 'Làm thế nào để học sinh nghe lời', 'Nguyễn Vũ Linh Chi1653387570.png', 'Dù giảng dạy tại trường hay là dạy gia sư tại nhà, ai cũng muốn cho học sinh của mình ngoan ngoãn, nghe lời. Điều này sẽ khiến cho việc truyền đạt kiến thức của mình trở nên dễ dàng hơn. Một số kinh nghiệm sau đây có thể khiến cho học sinh nghe lời bạn.', '<h2 id=\"hay_chuan_bi_quan_ao_nghiem_tuc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">1. H&atilde;y chuẩn bị quần &aacute;o nghi&ecirc;m t&uacute;c</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Chưa cần biết gia sư l&agrave; người giỏi cỡ n&agrave;o, nhưng đặc điểm chung của học sinh l&agrave; kh&ocirc;ng d&aacute;m đ&ugrave;a cợt với những người nghi&ecirc;m t&uacute;c. Kh&ocirc;ng cần thiết phải giống như qu&acirc;n nh&acirc;n, nhưng ăn mặc phải chỉn chu, gọn g&agrave;ng, lịch sự.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">C&aacute;i nh&igrave;n ban đầu tốt sẽ khiến học sinh c&oacute; thiện cảm với bạn, thậm ch&iacute; thần tượng phong c&aacute;ch của bạn.</p>\r\n<h2 id=\"phong_thai_cung_vo_cung_quan_trong\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">2. Phong th&aacute;i cũng v&ocirc; c&ugrave;ng quan trọng</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Bạn l&agrave; gia sư, l&agrave; một người thầy chứ kh&ocirc;ng c&ograve;n l&agrave; sinh vi&ecirc;n ngồi tr&ecirc;n ghế nh&agrave; trường. Bạn phải c&oacute; thần th&aacute;i của một gi&aacute;o vi&ecirc;n, lời n&oacute;i l&uacute;c phải thật c&oacute; uy, l&uacute;c lại thật th&acirc;n thiện để học sinh kh&ocirc;ng d&aacute;m \"giỡn mặt\".</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đ&ugrave;a cợt v&agrave; nể nang học sinh l&agrave; sai lầm m&agrave; rất nhiều gia sư mắc phải. Dần dần, học sinh sẽ cảm thấy bạn qu&aacute; hiền, dễ bắt nạt n&ecirc;n kh&ocirc;ng c&ograve;n coi lời n&oacute;i của bạn ra g&igrave;.</p>\r\n<h2 id=\"co_pham_chat_dao_duc_tot\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">3. C&oacute; phẩm chất đạo đức tốt</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Một người c&oacute; phẩm chất đạo đức, sống lương thiện sẽ dễ d&agrave;ng chinh phục được người kh&aacute;c, ngay cả học sinh của m&igrave;nh.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ngược lại, nếu bạn l&agrave; người c&oacute; những h&agrave;nh vi sai tr&aacute;i, thiếu t&ocirc;n trọng người kh&aacute;c, sử dụng lời lẽ miệt thị, x&uacute;c phạm học sinh, chắc chắc kh&oacute; được học sinh nể phục.</p>\r\n<h2 id=\"trau_doi_von_kien_thuc_cua_ban\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">4. Trau dồi vốn kiến thức của bạn</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đừng nghĩ chỉ cần giỏi kiến thức chuy&ecirc;n m&ocirc;n th&ocirc;i l&agrave; đủ. Trong giờ dạy, học sinh c&oacute; thể hỏi h&agrave;ng ng&agrave;n thứ tr&ecirc;n trời dưới biển để thỏa tr&iacute; t&ograve; m&ograve; v&agrave; l&agrave;m kh&oacute; bạn. Nếu bạn bị đ&aacute;nh gục ở giai đoạn n&agrave;y, ch&uacute;ng sẽ đắc &yacute; m&agrave; cho rằng bạn cũng \"thường th&ocirc;i\" kh&ocirc;ng c&oacute; g&igrave; phải sợ.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ch&iacute;nh v&igrave; vậy, kiến thức x&atilde; hội, tự nhi&ecirc;n, giới t&iacute;nh, địa l&yacute;, du lịch,... bạn n&ecirc;n tinh th&ocirc;ng c&agrave;ng nhiều c&agrave;ng tốt để \"ph&ocirc; trương thanh thế\" với học sinh.</p>\r\n<h2 id=\"hay_biet_giu_bi_mat\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">5. H&atilde;y biết giữ b&iacute; mật</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Trong khi học, học sinh sẽ v&ocirc; t&igrave;nh tiết lộ một số b&iacute; mật của ch&uacute;ng. Đừng kể những b&iacute; mật ấy với bố mẹ ch&uacute;ng hoặc người kh&aacute;c, nếu những b&iacute; mật đ&oacute; kh&ocirc;ng g&acirc;y hại g&igrave;. Chỉ khi bạn giữ b&iacute; mật, khiến ch&uacute;ng tin tưởng, bạn mới đ&aacute;ng để ch&uacute;ng nghe lời.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Tuy nhi&ecirc;n, nếu vấn đề b&iacute; mật nghi&ecirc;m trọng, h&atilde;y kh&eacute;o l&eacute;o n&oacute;i với phụ huynh để c&ugrave;ng gỡ rối cho học sinh. Nhắc lại l&agrave; phải thật kh&eacute;o l&eacute;o.</p>\r\n<h2 id=\"khong_bat_ep_qua_muc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">6. Kh&ocirc;ng bắt &eacute;p qu&aacute; mức</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ch&aacute;n học m&agrave; bị bắt &eacute;p qu&aacute; mức th&igrave; chỉ c&agrave;ng khiến học sinh bức bối v&agrave; chống đối m&agrave; th&ocirc;i. Vậy n&ecirc;n, bạn h&atilde;y t&igrave;m c&aacute;ch để học sinh nghe b&agrave;i giảng một c&aacute;ch hứng th&uacute; v&agrave; hiểu b&agrave;i ngay trong giờ. Sau giờ học, h&atilde;y giao c&aacute;c b&agrave;i tập nhẹ nh&agrave;ng li&ecirc;n quan đến ghi nhớ nhiều hơn l&agrave; ngồi giải b&agrave;i tập.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Chẳng hạn với m&ocirc;n m&ocirc;n To&aacute;n, y&ecirc;u cầu ch&uacute;ng học chuẩn c&ocirc;ng thức v&agrave; l&agrave;m những b&agrave;i trong khả năng, kh&ocirc;ng phải suy nghĩ qu&aacute; nhiều. M&ocirc;n văn, y&ecirc;u cầu ch&uacute;ng đọc trước nội dung văn bản của ng&agrave;y h&ocirc;m sau,...</p>\r\n<h2 id=\"vua_la_thay_vua_la_ban_cua_hoc_sinh\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">7. Vừa l&agrave; thầy, vừa l&agrave; bạn của học sinh</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Học sinh chỉ nghe lời bạn khi bạn đ&uacute;ng v&agrave; những g&igrave; bạn đem lại c&oacute; &iacute;ch cho ch&uacute;ng. Do đ&oacute;, l&agrave; người thầy, h&atilde;y cung cấp cho học sinh kiến thức bổ &iacute;ch cả chuy&ecirc;n m&ocirc;n lẫn kiến thức x&atilde; hội. Học bạn m&agrave; ch&uacute;ng biết th&ecirc;m thật nhiều thứ mới mẻ th&igrave; ch&uacute;ng sẽ v&ocirc; c&ugrave;ng th&iacute;ch th&uacute;.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Hết giờ giảng, h&atilde;y t&acirc;m sự với học sinh như một người bạn. Bạn cũng c&oacute; thể kể cho ch&uacute;ng nghe những kinh nghiệm bạn đ&atilde; trải qua thời học sinh. Những lời khuy&ecirc;n của bạn đừng theo kiểu \"em phải...\" m&agrave; thay bằng \"nếu l&agrave; anh/chị th&igrave; anh/chị sẽ...\".</p>\r\n<p class=\"mt-4\" style=\"box-sizing: border-box; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff; margin-top: 1.5rem !important;\">Tr&ecirc;n đ&acirc;y l&agrave; những c&aacute;ch để học sinh y&ecirc;u qu&yacute; v&agrave; nghe lời bạn. Nếu l&agrave;m hết những c&aacute;ch tr&ecirc;n m&agrave; vẫn kh&ocirc;ng c&oacute; hiệu quả th&igrave; bạn cần trao đổi với phụ huynh để t&igrave;m hướng xử l&yacute; ph&ugrave; hợp.</p>', '2022-05-24 10:19:30'),
(10, 1, 1, 'Các Để thuê Gia Sư', 'Phạm Tiến Thành công1653414531.png', 'làm sao để thuê gia sư ?', '<h2 id=\"chon_gia_su_phu_hop_voi_muc_tieu_hoc\" style=\"box-sizing: border-box; margin-top: 1.7rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.5rem; padding-top: 0.5rem; background-color: #ffffff;\">1. Chọn gia sư ph&ugrave; hợp với mục ti&ecirc;u học</h2>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Đầu ti&ecirc;n, bạn n&ecirc;n x&aacute;c định mục ti&ecirc;u, nhu cầu học Tiếng Anh của con l&agrave; g&igrave; trước, sau đ&oacute; mới chọn thu&ecirc; gia sư ph&ugrave; hợp với mục ti&ecirc;u đ&oacute; của con.</p>\r\n<h3 id=\"lay_lai_kien_thuc_goc\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Lấy lại kiến thức gốc</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Chương tr&igrave;nh học Tiếng Anh tr&ecirc;n trường l&agrave; một qu&aacute; tr&igrave;nh li&ecirc;n tục từ cấp Tiểu học đến hết lớp 12. Qu&aacute; tr&igrave;nh n&agrave;y bao gồm những kiến thức nối liền nhau, nếu con đang bị mất gốc sẽ kh&oacute; học l&ecirc;n cao.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">V&igrave; vậy, nếu con đang bị hổng kiến thức, h&atilde;y t&igrave;m gia sư gi&uacute;p con lấy lại kiến thức gốc trước rồi mới cho con học những kiến thức n&acirc;ng cao.</p>\r\n<h3 id=\"dat_diem_cao_trong_cac_ki_thi\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Đạt điểm cao trong c&aacute;c k&igrave; thi</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Ở trường lớp thường c&oacute; rất nhiều b&agrave;i kiểm tra, đ&aacute;nh gi&aacute;. Ngo&agrave;i ra c&ograve;n c&oacute; k&igrave; thi chuyển cấp, thi Chuy&ecirc;n. Để đạt được điểm thi tốt, kh&ocirc;ng những phải đảm bảo kiến thức cơ bản tr&ecirc;n lớp m&agrave; con cần học cả kiến thức n&acirc;ng cao hơn.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Để đảm bảo con đạt kết quả cao trong c&aacute;c k&igrave; thi bạn n&ecirc;n t&igrave;m một gia sư c&oacute; thể dạy cả kiến thức n&acirc;ng cao.</p>\r\n<h3 id=\"giao_tiep\" style=\"box-sizing: border-box; margin-top: 1.5rem; margin-bottom: 1rem; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; line-height: 1.5; color: #212529; font-size: 1.2rem; padding-top: 0.5rem; background-color: #ffffff;\">Giao tiếp</h3>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Một gia sư dạy tốt kiến thức n&acirc;ng cao tr&ecirc;n trường cũng chưa chắc c&oacute; thể dạy được giao tiếp. Bởi, ở lớp con chủ yếu học về ngữ ph&aacute;p nhiều hơn m&agrave; &iacute;t được học c&aacute;c kĩ năng nghe, n&oacute;i.</p>\r\n<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: #212529; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, \'Helvetica Neue\', Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; font-size: 16px; background-color: #ffffff;\">Do đ&oacute;, thay v&igrave; lựa chọn dựa v&agrave;o điểm thi Đại học của gia sư, bạn n&ecirc;n đ&aacute;nh gi&aacute; xem gia sư đ&oacute; c&oacute; n&oacute;i tiếng Anh tr&ocirc;i chảy kh&ocirc;ng, c&oacute; thường giao tiếp bằng tiếng Anh kh&ocirc;ng,&hellip;</p>', '2022-05-24 17:48:51');
